mapper.select:
select A.CRAFT_MAN_ID , A.CRAFT_MAN_NAME , ( select COMM_CD_KOR_NAME from kf99.tb_comm_cd tcc where COMM_CD = A.CRAFT_TYPE ) AS CRAFT_TYPE , A.CRAFT_VILLAGE_ID , A.CRAFT_LEVEL , A.CRAFT_HAND_SKILL , date_format( A.UPD_DT, '%Y-%m-%d %H:%s:%i') UPD_DT , B.* from kf99.TB_CRAFT_MAN_INFO A left outer join ( SELECT * FROM kf99.tb_village WHERE DEL_YN ='N' ) B on A.CRAFT_VILLAGE_ID = B.VILLAGE_ID WHERE 1=1
AND A.CRAFT_TYPE = #{craftsManType}
AND A.CRAFT_VILLAGE_ID = #{craftsManVillageName}
AND A.CRAFT_MAN_NAME LIKE '%${keyword}%'
AND B.VILLAGE_NAME LIKE '%${keyword}%'
order by A.CRAFT_TYPE, A.CRAFT_LEVEL DESC
