mapper.select:
SELECT A.EQUIP_ID , A.EQUIP_NAME , ( SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.EQUIP_LENT_STATUS ) EQUIP_LENT_STATUS , date_format( A.EQUIP_LENT_PERIOD_END, '%Y.%m') EQUIP_LENT_PERIOD_END , date_format( A.EQUIP_LENT_PERIOD_STRT, '%Y.%m') EQUIP_LENT_PERIOD_STRT , B.EQUIP_IMG , A.EQUIP_TYPE , A.EQUIP_LENT_STATUS AS EQUIP_LENT_STATUS_CODE , B.SYMB_IMAG_TYPE , A.EQUIP_LENT_NAME FROM TB_EQUIP_LENT A , TB_EQUIP_LENT_IMG B WHERE 1=1 AND A.EQUIP_ID = B.EQUIP_ID
AND A.EQUIP_TYPE = #{typeList}
AND A.EQUIP_LENT_STATUS = #{codeList}
AND A.EQUIP_NAME LIKE '%${keyword}%'
AND A.EQUIP_LENT_NAME LIKE '%${keyword}%'
ORDER BY A.EQUIP_LENT_STATUS ASC
