mapper.select:
SELECT A.HORSE_ID , A.HORSE_NAME , A.HORSE_NUM , A.HORSE_BURF_TYPE AS HORSE_BURF_TYPE_CODE , A.HORSE_HOUSE_BURF_TYPE AS HORSE_HOUSE_BURF_TYPE_CDOE , (SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.HORSER_LIMIT_CON AND COMM_CLSF_CD = 'HUCO' ) AS HORSER_LIMIT_CON , (SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.HORSE_BURF_TYPE AND COMM_CLSF_CD = 'ATKT' ) AS HORSE_BURF_TYPE , (SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.HORSE_LIFE AND COMM_CLSF_CD = 'HLIF' ) AS HORSE_LIFE , (SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.HORSE_HOUSE_BURF_TYPE AND COMM_CLSF_CD = 'SHBP' ) AS HORSE_HOUSE_BURF_TYPE , (SELECT COMM_CD_KOR_NAME FROM TB_COMM_CD WHERE COMM_CD = A.HORSE_BURF_PER_TYPE AND COMM_CLSF_CD = 'SHBU' ) AS HORSE_BURF_PER_TYPE , NVL(B.MAX_SPED, 0) MAX_SPED , NVL(B.MAX_AGIL, 0) MAX_AGIL , NVL(B.MAX_FORCE, 0) MAX_FORCE , NVL(B.MAX_MAGIC_FORCE, 0) MAX_MAGIC_FORCE , NVL(B.BIG_SHOP_PRICE,'정보없음') BIG_SHOP_PRICE FROM TB_HORSE A, TB_HORSE_DETL B WHERE 1=1 AND A.HORSE_ID = B.HORSE_ID(+) AND A.DEL_YN = 'N'
AND HORSER_LIMIT_CON = #{rideLimitType}
AND HORSE_HOUSE_BURF_TYPE = #{horseHouseBurpType}
AND HORSE_BURF_TYPE = #{horseBurpType}
AND HORSE_LIFE = #{horseLifeType}
AND HORSE_BURF_PER_TYPE = #{horseHouseBurpPercent}
ORDER BY A.HORSE_NUM
