mapper.select:
SELECT VILLAGE_ID
FROM sys.TB_VILLAGE
WHERE VILLAGE_NAME= #{name}

mapper.insert:
INSERT INTO sys.TT_YANG_WAR_INFO (
                    VILLAGE_ID
				  , STAT_HH_STND
				  , KILL_YANG
				  , WAR_TIME_TYPE
				  , REG_DT
)
VALUES
(
 				#{villageId}
				  , #{time}
				  , #{killYang}
			      , #{timeType}
  				  , NOW()
)

INSERT INTO sys.TT_VILLAGE_COUNT_INFO (
                    VILLAGE_ID
				  , STAT_DD_STND
				  , HUMAN_CNT
				  , REG_DT
)
VALUES
(
 				#{villageId}
				  , #{time}
				  , #{humanCnt}
				  , NOW()
)
