mapper.insert:
                INSERT INTO kf99.tb_station(
						  FR_CODE
						, STATION_NM
						, STATION_NM_ENG
						, STATION_CD
                        , LINE_NUM
                        , STATION_NM_JPN
                        , STATION_NM_CHN
                       , REG_DT
                       , UPD_DT
                    )
                    VALUES (
                             #{frCode}
							   , #{stationNm}
							   , #{stationNmEng}
							   , #{stationCd}
							, #{lineNum}
							, #{stationNmJpn}
							, #{stationNmChn}
							, now()
							, now()
                           )ON DUPLICATE KEY
        update
        	STATION_NM = #{stationNm},
                    	STATION_NM_ENG = #{stationNmEng},
			      		STATION_CD = #{stationCd},
                        LINE_NUM = #{lineNum},
                        STATION_NM_JPN = #{stationNmJpn},
                        STATION_NM_CHN = #{stationNmChn},
                       	UPD_DT = now()
