mapper.insert:
INSERT INTO sys.TB_USER
VALUES(
       #{userId}
	, #{encryptPwd}
	, #{userName}
	, null
	, null
	, null
	, null
	, 'N'
	, #{cityYn}
	, 'N'
	, null
	, null
	, #{cityName}
	, NOW()
	, NOW()
	  )

mapper.update:
UPDATE sys.TB_USER
   SET USER_NAME = #{userName}
     , CITY_YN   = #{cityYn}
     , CITY_USER_NAME = #{cityName}
     , UPD_DT = NOW()
WHERE
	USER_ID = #{userId}

UPDATE sys.TB_USER
   SET
      USER_PWD = #{encryptPwd}
     ,  UPD_DT = NOW()
WHERE
	USER_ID = #{userId}
